package ejerciciosDeTodoTipo;

public class AppendDentroDeBucleFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		StringBuilder sb = new StringBuilder();
		
		for(int i = 1; i <= 5; i++) {
			sb.append(i + " ");
		}

		
		System.out.println(sb);
		
	}

}
